import sailpoint.object.* ;
import java.util.Iterator;
import java.util.List;

import sailpoint.object.Certifiable;



if (identity.getAttribute("FINSERVID").length()>6) { 

    Iterator it = items.iterator();
    while (iter.hasNext()) {

        sailpoint.object.Certifiable item = iter.next(); 
        if(item.getNativeIdentity().length()>6){
                 System.out.println("native identity is : "+item.getNativeIdentity());
             itemsToExclude.add(item);
             it.remove();
        }
   }

}
